import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Dashboard() {
  const [brands, setBrands] = useState([]);
  const [newBrand, setNewBrand] = useState('');

  const fetchBrands = async () => {
    const res = await axios.get('http://localhost:5000/api/brands');
    setBrands(res.data);
  };

  useEffect(() => {
    fetchBrands();
  }, []);

  const handleAdd = async () => {
    if (!newBrand.trim()) return;
    await axios.post('http://localhost:5000/api/brands', { name: newBrand });
    setNewBrand('');
    fetchBrands();
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/api/brands/${id}`);
    fetchBrands();
  };

  const handleUpdate = async (id, currentName) => {
    const updated = prompt('Update brand name:', currentName);
    if (updated) {
      await axios.put(`http://localhost:5000/api/brands/${id}`, { name: updated });
      fetchBrands();
    }S
  };

   // ðŸšª Logout Function
  const handleLogout = () => {
    localStorage.removeItem('token');
    alert('Logout successful');
    navigate('/');
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Global Majestic Car Brands</h2>
      <input
        placeholder="New brand name"
        value={newBrand}
        onChange={e => setNewBrand(e.target.value)}
        style={{ marginRight: '1rem' }}
      />
      <button onClick={handleAdd}>Add Brand</button>

      <ul style={{ marginTop: '2rem' }}>
        {brands.map(brand => (
          <li key={brand._id}>
            {brand.name}
            <button onClick={() => handleUpdate(brand._id, brand.name)} style={{ marginLeft: '1rem' }}>Edit</button>
            <button onClick={() => handleDelete(brand._id)} style={{ marginLeft: '0.5rem' }}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
